var _ = require('../index.js');

var iterator = function (value, next) {
		setTimeout(function () {
			console.log(value);
			next(null, value);
		}, 100);
	},
	done = function (err, results) {
		console.log(err, results);
	},
	queue = [];

queue.push(1);
queue = _.series(queue, iterator, done);
queue.push(2);
queue.push(3);
queue.push(4);
queue.push(5);
queue.push(6);
queue.push(7);
